function validate(FieldContext, attributeValue, changedValue, errorMessage) {
importClass(Packages.com.netegrity.ims.tabhandlers.ProcessStep);
importClass(Packages.com.netegrity.ims.tabhandlers.ErrorLevel);
importClass(Packages.com.netegrity.ims.task.TaskErrorObject);
if (attributeValue=="true" && 
	FieldContext.getTaskSession().getSubject().getOriginalAttribute("usingStrongSynchronization")!="true") 
{
FieldContext.getTaskSession().addErrorObject(new TaskErrorObject(
					FieldContext.getLocalizer("provisioning.resourceBundles.improvisioning")
							.getLocalizedString("warning.message.strong.synchronization"),
					"", ProcessStep.INPUTVALIDATE, ErrorLevel.WARNING,
					FieldContext.getTaskSession().getTask().getTabs().get(0).getTag()));
}
return true;
}

function init(FieldContext)
{
    if (FieldContext.getValue() == 'true')
    {
    	//FieldContext.hideField("defaultUserUpdateMap");
    	FieldContext.enableField("userUpdateMap");
    }
    else
    {
    	//FieldContext.showField("defaultUserUpdateMap");
    	FieldContext.disableField("userUpdateMap");
    }

    return true;
}

function validate(FieldContext, attributeValue, changedValue, errorMessage) {
    if (attributeValue == 'true')
    {
    	//FieldContext.hideField("defaultUserUpdateMap");
    	FieldContext.enableField("userUpdateMap");
    }
    else
    {
    	//FieldContext.showField("defaultUserUpdateMap");
    	FieldContext.disableField("userUpdateMap");
    }

    return true;
}

